css:
body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 0;
    line-height: 1.6;
}
.letterhead {
    background-color: #2C3E50;
    color: white;
    padding: 20px;
    text-align: center;
}
.letterhead h1 {
    margin: 0;
    font-size: 36px;
}
.letterhead p {
    color: white;
    margin: 0;
    font-size: 18px;
}
.container {
    padding: 20px;
}
.row {
    margin-bottom: 10px;
}
.text-right {
    text-align: right;
}
table {
    width: 95%;
    margin-top: 20px;
    border-collapse: collapse;
}
table, th, td {
    border: 1px solid #ddd;
}
th, td {
    padding: 8px;
    text-align: center;
}
th {
    background-color: #f4f4f4;
}
@page {
    size: A4;
    margin: 20mm;
}
@media print {
    body {
        margin: 0;
        padding: 0;
    }
    .letterhead h1 {
        font-size: 30px;
    }
    .letterhead p {
        font-size: 16px;
    }
    .container {
        padding: 10px;
        width: 95%;
    }
    .row {
        margin-bottom: 5px;
    }
    table {
        width: 95%;
        margin-top: 20px;
        border-collapse: collapse;
    }
    th, td {
        padding: 6px;
        text-align: center;
        font-size: 12px;
    }
    th {
        background-color: black;
        color: white;
    }
}
